$(function() {
	var a = $(".main-nav-btn"),
		i = $(".main-wrap"),
		s = ($(".icon-gear"), $(".main-content"));
	a.on("click", function() {
		i.hasClass("nav-full") ? i.removeClass("nav-full").addClass("nav-mini") : i.removeClass("nav-mini").addClass("nav-full")
	});
	var t = $(".main-sidebar-btn"),
		e = $(".main-sidebar"),
		o = $(".main-sidebar-btn-wrap");
	t.on("click", function() {
		var n = $(this);
		n.hasClass("sidebar-btn-off") ? (e.addClass("main-sidebar-mini"), n.hide().next().show(), s.addClass("main-content-full"), o.addClass("main-sidebar-btn-wrap-on")) : (e.removeClass("main-sidebar-mini"), n.hide().prev().show(), s.removeClass("main-content-full"), o.removeClass("main-sidebar-btn-wrap-on"))
	});
	var d = $(".main-nav-list").find("li"),
		l = $(".main-nav"),
		v = "";
	d.on({
		mouseover: function() {
			if (i.hasClass("nav-mini")) {
				var n = $(this).find(".nav-text").text(),
					a = $(this).index(),
					s = 120 + 40 * a - l.scrollTop();
				v = '<div class="nav-tip" style="top:' + s + 'px">' + n + "</div>", $("body").append(v)
			}
		},
		mouseout: function() {
			var n = $(this).find(".nav-text").text();
			i.hasClass("nav-mini") && $("body").find(".nav-tip") && $("body").find(".nav-tip").text() == n && $("body").find(".nav-tip").remove()
		}
	})
});